package pl.coderslab.BWF.controller;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import pl.coderslab.BWF.converter.SoccerMatchConverter;import pl.coderslab.BWF.services.MatchService;import pl.coderslab.BWF.webClient.matches.MatchesClient;@Slf4j@Controller@RequiredArgsConstructor@RequestMapping("/matches")public class MatchController {    private final MatchService matchService;    private final SoccerMatchConverter converter;    private final MatchesClient matchesClient;    @GetMapping("/matchList")    private void matchList() {        matchService.updateAllMatchDataBase(converter.convertDtoToMatchEntity(matchesClient.getMatches()));    }}