package pl.coderslab.BWF.controller;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.bind.annotation.RestController;import pl.coderslab.BWF.converter.SoccerMatchConverter;import pl.coderslab.BWF.entity.SoccerMatch;import pl.coderslab.BWF.services.MatchService;import pl.coderslab.BWF.webClient.matches.MatchesClient;import java.util.List;@Slf4j@RestController@RequiredArgsConstructor@RequestMapping("/matches")public class MatchController {    private final MatchService matchService;    private final SoccerMatchConverter converter;    private final MatchesClient matchesClient;    @GetMapping("/allMatches")    @ResponseBody    public List<SoccerMatch> allMatches() {        return matchService.getMatches();    }    @GetMapping("/addMatches")    @ResponseBody    public void addMatches() {        List<SoccerMatch> matches= converter.convertDtoToMatchEntity(matchesClient.getMatches());        matchService.addMatches(matches);    }}