package pl.coderslab.BWF.controller;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import pl.coderslab.BWF.converter.SoccerMatchConverter;import pl.coderslab.BWF.entity.SoccerMatch;import pl.coderslab.BWF.converter.DateConverter;import pl.coderslab.BWF.services.MatchService;import pl.coderslab.BWF.webClient.matches.MatchesClient;import java.util.Comparator;import java.util.List;@Slf4j@Controller@RequiredArgsConstructor@RequestMapping("/matches")public class MatchController {    private final MatchService matchService;    private final SoccerMatchConverter converter;    private final MatchesClient matchesClient;    private final DateConverter dateConverter;    @GetMapping("/matchList")    private String matchList(Model model) {        List<SoccerMatch> soccerMatchList = converter.convertDtoToMatchEntity(matchesClient.getMatches());        matchService.updateAllMatchDataBase(soccerMatchList);        soccerMatchList.sort(Comparator.comparing(SoccerMatch::getMatchDate).reversed());        model.addAttribute("soccerMatchList", soccerMatchList);        model.addAttribute("date", dateConverter.getDateNow());        return "matches/list";    }}