package pl.coderslab.BWF.converter;import org.joda.time.DateTime;import org.joda.time.format.DateTimeFormat;import org.joda.time.format.DateTimeFormatter;import org.springframework.stereotype.Component;@Componentpublic class DateConverter {    public String getDateNow() {        DateTime d = new DateTime();        DateTimeFormatter fmt = DateTimeFormat.forPattern("yyyy-MM-dd, HH:mm");        return d.toString(fmt);    }    public String changeDateToPattern(String date) {        DateTime d = DateTime.parse(date);        DateTimeFormatter fmt = DateTimeFormat.forPattern("yyyy-MM-dd, HH:mm");        return d.toString(fmt);    }}