package pl.coderslab.BWF.model;import lombok.Builder;import lombok.Getter;import org.joda.time.DateTime;import org.joda.time.format.DateTimeFormat;import org.joda.time.format.DateTimeFormatter;@Getter@Builderpublic class SoccerMatchDto {    private Long id;    private String stage;    private int homeTeamId;    private String homeTeam;    private int homeTeamScore;    private int awayTeamId;    private String awayTeam;    private int awayTeamScore;    private String utcDate;    private String score;    private String group;    public String getUtcDate() {        DateTime d=DateTime.parse(utcDate);        DateTimeFormatter fmt= DateTimeFormat.forPattern("yyyy-MM-dd, HH:mm");        return d.toString(fmt);    }    @Override    public String toString() {        return "SoccerMatchDto{" +                "id=" + id +                ", utcDate='" + utcDate + '\'' +                ", score='" + score + '\'' +                ", stage='" + stage + '\'' +                ", homeTeamId=" + homeTeamId +                ", homeTeam='" + homeTeam + '\'' +                ", homeTeamScore=" + homeTeamScore +                ", awayTeamScore=" + awayTeamScore +                ", awayTeam='" + awayTeam + '\'' +                ", awayTeamId=" + awayTeamId +                ", group='" + group + '\'' +                '}';    }}