package pl.coderslab.BWF.services;import org.springframework.stereotype.Service;import pl.coderslab.BWF.entity.BetGroup;import pl.coderslab.BWF.repository.BetGroupRepository;import java.util.List;@Servicepublic class BetGroupService {    private final BetGroupRepository betGroupRepository;    public BetGroupService(BetGroupRepository betGroupRepository) {        this.betGroupRepository = betGroupRepository;    }    public List<BetGroup> showBetGroups() {        return betGroupRepository.findAll();    }    public void addBetGroup(BetGroup group) {        betGroupRepository.save(group);    }    public void updateBetGroup(BetGroup group) {        betGroupRepository.save(group);    }    public BetGroup getBetGroup(Long id) {        return betGroupRepository.findById(id).get();    }    public void deleteBetGroup(Long id) {        betGroupRepository.deleteById(id);    }}