package pl.coderslab.BWF.services;import org.springframework.stereotype.Service;import pl.coderslab.BWF.entity.BetGroup;import pl.coderslab.BWF.repository.BetGroupRepository;import java.util.List;import java.util.Optional;@Servicepublic class BetGroupService {    private final BetGroupRepository betGroupRepository;    public BetGroupService(BetGroupRepository betGroupRepository) {        this.betGroupRepository = betGroupRepository;    }    public List<BetGroup> showGroups() {        return betGroupRepository.findAll();    }    public void addGroup(BetGroup group) {        betGroupRepository.save(group);    }    public void updateGroup(BetGroup group) {        betGroupRepository.save(group);    }    public Optional<BetGroup> getGroup(Long id) {        return betGroupRepository.findById(id);    }    public void deleteGroup(Long id) {        betGroupRepository.deleteById(id);    }}