package pl.coderslab.BWF.services;import pl.coderslab.BWF.entity.UserGroup;import pl.coderslab.BWF.repository.UserGroupRepository;import java.util.List;import java.util.Optional;public class GroupService {    private final UserGroupRepository userGroupRepository;    public GroupService(UserGroupRepository userGroupRepository) {        this.userGroupRepository = userGroupRepository;    }    public List<UserGroup> showGroups() {        return userGroupRepository.findAll();    }    public void addGroup(UserGroup group) {        userGroupRepository.save(group);    }    public void updateGroup(UserGroup group) {        userGroupRepository.save(group);    }    public Optional<UserGroup> get(Long id) {        return userGroupRepository.findById(id);    }    public void deleteGroup(Long id) {        userGroupRepository.deleteById(id);    }}