package pl.coderslab.BWF.services;import org.springframework.stereotype.Service;import pl.coderslab.BWF.entity.BetGroup;import pl.coderslab.BWF.entity.User;import pl.coderslab.BWF.entity.UserGroupAccount;import pl.coderslab.BWF.repository.UserGroupAccountRepository;@Servicepublic class UserGroupAccountService {    private final UserGroupAccountRepository userGroupAccountRepository;    public UserGroupAccountService(UserGroupAccountRepository userGroupAccountRepository) {        this.userGroupAccountRepository = userGroupAccountRepository;    }    public void addUserToBetGroup(User user, BetGroup betGroup) {        UserGroupAccount userGroupAccount=new UserGroupAccount();        userGroupAccount.setUser(user);        userGroupAccount.setBetGroup(betGroup);        userGroupAccountRepository.save(userGroupAccount);    }}